import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import api from "../api/client";
import Input from "./Input";

const gradientStyle = {
  background: "linear-gradient(135deg, #3b0dab, #6f42c1, #6610f2)",
};

const AuthCard = ({ mode = "login" }) => {
  const navigate = useNavigate();
  const [form, setForm] = useState({ fullName: "", email: "", password: "" });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    if (errors[e.target.name]) setErrors({ ...errors, [e.target.name]: "" });
  };

  const validate = () => {
    const err = {};
    if (mode === "signup" && form.fullName.trim().length < 2) {
      err.fullName = "Please enter your name";
    }
    if (!/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/.test(form.email)) {
      err.email = "Enter a valid email";
    }
    if (form.password.length < 6) {
      err.password = "Password must be at least 6 characters";
    }
    return err;
  };

  const submit = async (e) => {
    e.preventDefault();
    const err = validate();
    if (Object.keys(err).length) {
      setErrors(err);
      return;
    }

    setLoading(true);
    try {
      const endpoint = mode === "login" ? "/auth/login" : "/auth/signup";
      const payload =
        mode === "login"
          ? { email: form.email, password: form.password }
          : { fullName: form.fullName, email: form.email, password: form.password };

      await api.post(endpoint, payload);
      toast.success(mode === "login" ? "Logged in successfully!" : "Account created!");
      navigate("/dashboard");
    } catch (err) {
      toast.error(err.response?.data?.error || "Something went wrong");
    } finally {
      setLoading(false);
    }
  };

  const googleAuth = () => {
    window.location.href = `${api.defaults.baseURL}/auth/google`;
  };

  return (
    <div
      className="d-flex align-items-center justify-content-center min-vh-100 text-dark"
      style={gradientStyle}
    >
      <div className="card shadow-lg p-4" style={{ width: "100%", maxWidth: 420, borderRadius: "1rem" }}>
        <div className="text-center mb-4">
          <img
            src="https://www.logo.wine/a/logo/IBM/IBM-Logo.wine.svg"
            alt="Websphere Technology"
            style={{ width: "120px", marginBottom: "10px" }}
          />
          <h4 className="fw-bold text-primary">
            {mode === "login" ? "Login to Websphere Portal" : "Create your Websphere Account"}
          </h4>
        </div>

        <form onSubmit={submit} className="vstack gap-3">
          {mode === "signup" && (
            <>
              <Input
                placeholder="Full Name"
                name="fullName"
                value={form.fullName}
                onChange={handleChange}
                className={errors.fullName ? "is-invalid" : ""}
              />
              {errors.fullName && <div className="invalid-feedback d-block">{errors.fullName}</div>}
            </>
          )}

          <Input
            type="email"
            placeholder="Email"
            name="email"
            value={form.email}
            onChange={handleChange}
            className={errors.email ? "is-invalid" : ""}
          />
          {errors.email && <div className="invalid-feedback d-block">{errors.email}</div>}

          <Input
            type="password"
            placeholder="Password"
            name="password"
            value={form.password}
            onChange={handleChange}
            className={errors.password ? "is-invalid" : ""}
          />
          {errors.password && <div className="invalid-feedback d-block">{errors.password}</div>}

          <button
            type="submit"
            className="btn btn-primary w-100"
            disabled={loading}
            style={{ borderRadius: "50px", fontWeight: "bold" }}
          >
            {loading ? "Please waitâ€¦" : mode === "login" ? "Login" : "Sign Up"}
          </button>
        </form>

        <div className="d-flex align-items-center my-3">
          <hr className="flex-grow-1" />
          <span className="px-2 text-muted small">OR</span>
          <hr className="flex-grow-1" />
        </div>

        <button
          className="btn btn-outline-dark w-100 d-flex align-items-center justify-content-center gap-2"
          onClick={googleAuth}
          style={{ borderRadius: "50px" }}
        >
          <img
            src="https://www.svgrepo.com/show/475656/google-color.svg"
            alt="Google"
            style={{ width: 20, height: 20 }}
          />
          <span>Continue with Google</span>
        </button>

        <p className="text-center mt-3 mb-0 small text-muted">
          {mode === "login" ? (
            <>
              Don&apos;t have an account?{" "}
              <a href="/signup" className="fw-semibold text-decoration-none text-primary">
                Sign up
              </a>
            </>
          ) : (
            <>
              Already registered?{" "}
              <a href="/login" className="fw-semibold text-decoration-none text-primary">
                Log in
              </a>
            </>
          )}
        </p>
      </div>
    </div>
  );
};

export default AuthCard;
