// src/components/AuthCard.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import api from "../api/client";
import Input from "./Input";

const gradientStyle = {
  background:
    "linear-gradient(135deg, rgba(99,102,241,1) 0%, rgba(147,51,234,1) 50%, rgba(236,72,153,1) 100%)",
};

const AuthCard = ({ mode = "login" }) => {
  const navigate = useNavigate();
  const [form, setForm] = useState({ fullName: "", email: "", password: "" });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const submit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const endpoint = mode === "login" ? "/auth/login" : "/auth/signup";
      const payload =
        mode === "login"
          ? { email: form.email, password: form.password }
          : { fullName: form.fullName, email: form.email, password: form.password };

      await api.post(endpoint, payload);
      toast.success(mode === "login" ? "Logged in successfully!" : "Account created!");
      navigate("/dashboard");
    } catch (err) {
      toast.error(err.response?.data?.error || "Something went wrong");
    } finally {
      setLoading(false);
    }
  };

  const googleAuth = () => {
    window.location.href = `${api.defaults.baseURL}/auth/google`;
  };

  return (
    <div className="d-flex align-items-center justify-content-center min-vh-100" style={gradientStyle}>
      <div className="card shadow-lg" style={{ maxWidth: 420, width: "100%" }}>
        <div className="card-body p-4">
          <h2 className="card-title text-center mb-4">
            {mode === "login" ? "Welcome Back!" : "Create an Account"}
          </h2>

          <form onSubmit={submit} className="vstack gap-3">
            {mode === "signup" && (
              <Input
                placeholder="Full Name"
                name="fullName"
                value={form.fullName}
                onChange={handleChange}
              />
            )}
            <Input
              type="email"
              placeholder="Email"
              name="email"
              value={form.email}
              onChange={handleChange}
            />
            <Input
              type="password"
              placeholder="Password"
              name="password"
              value={form.password}
              onChange={handleChange}
            />

            <button type="submit" className="btn btn-primary w-100" disabled={loading}>
              {loading ? "Please wait…" : mode === "login" ? "Login" : "Sign Up"}
            </button>
          </form>

          <div className="d-flex align-items-center my-3">
            <hr className="flex-grow-1" />
            <span className="px-2 text-muted small">OR</span>
            <hr className="flex-grow-1" />
          </div>

          <button className="btn btn-outline-secondary w-100 d-flex align-items-center justify-content-center gap-2" onClick={googleAuth}>
            <img src="https://www.svgrepo.com/show/475656/google-color.svg" alt="Google" style={{ width: 20, height: 20 }} />
            Continue with Google
          </button>

          <p className="text-center mt-3 mb-0 small text-muted">
            {mode === "login" ? (
              <>
                Don&apos;t have an account? <a href="/signup">Sign up</a>
              </>
            ) : (
              <>
                Already registered? <a href="/login">Log in</a>
              </>
            )}
          </p>
        </div>
      </div>
    </div>
  );
};

export default AuthCard;
