// src/pages/Dashboard.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api/client";

const AUTH_STORAGE_KEY = "websphere:user";

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(() => {
    // grab from localStorage first
    const cached = localStorage.getItem(AUTH_STORAGE_KEY);
    return cached ? JSON.parse(cached) : null;
  });

  // refresh from back‑end (covers page reloads & Google login)
  useEffect(() => {
    if (user) return; // already have data
    api
      .get("/auth/me")          // needs back‑end endpoint below
      .then(({ data }) => {
        setUser(data.user);
        localStorage.setItem(AUTH_STORAGE_KEY, JSON.stringify(data.user));
      })
      .catch(() => navigate("/login"));
  }, [user, navigate]);

  if (!user) {
    return (
      <div className="d-flex min-vh-100 align-items-center justify-content-center">
        <div className="spinner-border" role="status" />
      </div>
    );
  }

  return (
    <div className="container py-5">
      <div className="col-md-6 mx-auto text-center">
        <h1 className="fw-bold mb-3">Welcome&nbsp;{user.fullName || "User"}!</h1>
        <p className="lead text-muted mb-4">Email: {user.email}</p>
        <button
          className="btn btn-outline-danger"
          onClick={() => {
            localStorage.removeItem(AUTH_STORAGE_KEY);
            api.post("/auth/logout").finally(() => navigate("/login"));
          }}
        >
          Logout
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
